// 本地模拟（本地mock数据）
const SIMULATION = 0
// 开发 (请求线上模拟数据)
const DEVELOPMENT = 1
// 测试（请求线上数据）
const DEBUG = 2
// 生产
const PRODUCTION = 4

// mock 数据地址
const MOCK_HOST = 'localhost:3000'
// mock 数据地址
const DEV_HOST = '127.0.0.1:3000'
// 生产数据地址
const PRO_HOST = '127.0.0:9001'

const LOC_PROTOCOL = location.protocol + '//'
const LOC_HOST = location.host
// host === hostname:port
const LOC_HOSTNAME = location.hostname

const LOC_RES = {
  module: 'loc',
  protocol: LOC_PROTOCOL,
  host: LOC_HOST,
  ver: ''
}
const APP_RES = {
  module: 'app',
  protocol: 'http://',
  ver: ''
}

/**
 * @constant {number} ENV
 */
const ENV = (() => {
  if (__DEV__) {
    switch (LOC_HOSTNAME) {
      case 'localhost':
        return SIMULATION
      case '127.0.0.1':
        return DEVELOPMENT
      default:
        return DEBUG
    }
  }
  return PRODUCTION
})()

switch (ENV) {
  case SIMULATION:
    APP_RES.host = MOCK_HOST
    break
  case DEVELOPMENT:
    APP_RES.host = DEV_HOST
    break
  case DEBUG:
    APP_RES.host = PRO_HOST
    break
  default:
    APP_RES.host = LOC_HOST
}

/**
 * @constant {string} DATETIME_FORMAT 默认的时间日期格式
 */
const DATETIME_FORMAT = 'yyyy-MM-dd hh:mm:ss'

/**
 * @constant {string} DATE_FORMAT 默认的日期格式
 */
const DATE_FORMAT = 'yyyy-MM-dd'

/**
 * @constant {string} TIME_FORMAT 默认的时间格式
 */
const TIME_FORMAT = 'hh:mm:ss'

/**
 * @constant {string} TOAST_DURATION 默认提示信息显示毫秒数
 */
const TOAST_DURATION = 3000

/**
 * @constant {string}  透明图片
 */
const DEFAULT_AVATAR = 'data:image/png;base64,'
/**
 * 单位
 * @type {Object}
 */
const UNITS = {
  'SECOND': 'SECOND',
  'MINUTE': 'MINUTE',
  'HOUR': 'HOUR',
  'KM': 'KM',
  'METER': 'METER',
  'GROUP': 'GROUP',
  'NUM': 'NUM',
  'CLUD': 'CLUD'
}

const config = {
  SIMULATION,
  DEVELOPMENT,
  DEBUG,
  PRODUCTION,
  ENV,
  LOC_RES,
  APP_RES,
  DATETIME_FORMAT,
  DATE_FORMAT,
  TIME_FORMAT,
  TOAST_DURATION,
  DEFAULT_AVATAR,
  UNITS
}

Object.keys(config).map(key => {
  if (/_RES$/.test(key)) {
    const { protocol, host, ver } = config[key]
    config[key].base = `${protocol}${host}${ver ? `/${ver}` : ''}`
  }
})

export default config
